import { useState } from "react";
import { PageCell } from "./PageCell";
export const Pagination = ({ total, selected, onPageChange }) => {
  console.log(total,selected,onPageChange);
  const [pages] = useState(new Array(total).fill(1));
  console.log(pages)
  return <div className="pageContainer" style={{display:"flex"}}>
    {pages.map((el,index)=>(<div>
      <PageCell  num={index} style={{margin}}  />
    </div>))}
  </div>;
};
