import { useState } from "react";
export const useCounter = (init) => {
  console.log(init);
  const [count, setV] = useState(init.initialValue);

  const incCount = (num) => {
    if (!num) {
      num = 1;
    }
    if(count<init.maxValue)
    {
        setValue((c) => c + num);
    }
    if (count > init.maxValue) {
      setValue(init.maxValue);
    }

  };
  const decCount = (num) => {
      if (!num) {
        num = 1;
      }
      if(count>init.minValue){
          setValue((c) => c - num);
      }
      if (count < init.minValue) {
        setValue(init.minValue);
      }

  }

  return { count, incCount, decCount };
};
